/**
 * Об'єднаний тип для всіх ресурсів.
 */
export type Resource =
  | BookResource
  | ArticleResource
  | MethodicalResource
  | ManualResource
  | ConferenceResource
  | DissertationResource
  | AbstractResource
  | ReportResource;
